```
// 定义一些常量和变量
const sessionApi = 'https://chat.openai.com/api/auth/session'; // 获取会话的API地址
const apiUrl = 'https://chat.openai.com/backend-api'; // 获取聊天数据的API地址
let accessToken = null; // 存储会话令牌
let chatId = null; // 存储聊天ID
let chatData = null; // 存储聊天数据

// 定义一个函数，用于获取会话令牌
async function fetchSession() {
  try {
    let response = await fetch(sessionApi); // 发送请求
    let session = await response.json(); // 解析响应
    accessToken = session.accessToken; // 获取会话令牌
    console.log('Session fetched:', accessToken); // 打印日志
  } catch (error) {
    console.error('Error fetching session:', error); // 打印错误
  }
}

// 定义一个函数，用于获取聊天ID
function fetchChatId() {
  try {
    const match = location.pathname.match(/^\/(?:share|c)\/([a-z0-9-]+)/i); // 从当前网址中匹配聊天ID
    chatId = match[1]; // 获取聊天ID
    console.log('Chat ID fetched:', chatId); // 打印日志
  } catch (error) {
    console.error('Error fetching chat ID:', error); // 打印错误
  }
}

// 定义一个函数，用于获取聊天数据
async function fetchChatData() {
  try {
    const url = apiUrl + '/conversation/' + chatId; // 构造请求地址
    const options = {
      headers: {
        accept: '*/*',
        acceptlanguage: 'zh-TW,zh;q=0.9,en;q=0.8,zh-CN;q=0.7',
        authorization: `Bearer ${accessToken}`, // 设置授权头部，使用会话令牌
      },
    };
    let response = await fetch(url, options); // 发送请求
    let data = await response.json(); // 解析响应
    chatData = data.conversation.messages; // 获取聊天数据，是一个消息数组
    console.log('Chat data fetched:', chatData); // 打印日志
  } catch (error) {
    console.error('Error fetching chat data:', error); // 打印错误
  }
}

// 定义一个函数，用于将聊天数据转换为可视化所需的格式
function transformChatData() {
  try {
    let nodes = []; // 存储节点数组，每个节点代表一个消息
    let links = []; // 存储边数组，每条边代表两个消息之间的关系

    for (let i = 0; i < chatData.length; i++) {
      let message = chatData[i]; // 获取第i个消息

      // 创建一个节点对象，包含消息的id、内容、作者、角色、时间等属性
      let node = {
        id: message.id,
        text: message.text,
        author: message.author.name,
        role: message.author.role,
        time: message.created_at,
      };

      nodes.push(node); // 将节点对象添加到节点数组中

      if (i > 0) {
        let previousMessage = chatData[i - 1]; // 获取第i-1个消息

        // 创建一条边对象，包含两个消息的id、时间差等属性
        let link = {
          source: previousMessage.id,
          target: message.id,
          timeDiff: message.created_at - previousMessage.created_at,
        };

        links.push(link); // 将边对象添加到边数组中
      }
    }

    return { nodes, links }; // 返回节点和边的对象
  } catch (error) {
    console.error('Error transforming chat data:', error); // 打印错误
  }
}

// 定义一个函数，用于与popup和content script通信
function handleMessage(request, sender, sendResponse) {
  try {
    if (request.type === 'start') {
      // 如果收到popup发送的开始可视化的请求
      fetchSession(); // 获取会话
      fetchChatId(); // 获取聊天ID
      fetchChatData().then(() => {
        // 获取聊天数据，并在完成后执行以下操作
        let data = transformChatData(); // 转换聊天数据
        sendResponse(data); // 将数据发送给content script
      });
    } else if (request.type === 'stop') {
      // 如果收到popup发送的停止可视化的请求
      sendResponse('Visualization stopped'); // 发送响应
    }
  } catch (error) {
    console.error('Error handling message:', error); // 打印错误
  }
}

// 监听来自popup和content script的消息
chrome.runtime.onMessage.addListener(handleMessage);
