        return '#0000ff';
      case 'user':
        return '#00ff00';
      // Add more cases if there are other roles
      default:
        return '#000000'; // Default color
    }
  } else {
    return '#000000'; // Default color
  }
};

// 定义一个函数，用于创建和更新可视化图表
function drawChart(data) {
  try {
    let width = window.innerWidth; // 获取窗口的宽度
    let height = window.innerHeight; // 获取窗口的高度

    let svg = d3.select('body').append('svg') // 在body元素中添加一个svg元素
      .attr('width', width) // 设置svg的宽度
      .attr('height', height) // 设置svg的高度
      .style('position', 'absolute') // 设置svg的位置为绝对定位
      .style('top', 0) // 设置svg的上边距为0
      .style('left', 0) // 设置svg的左边距为0
      .style('z-index', -1); // 设置svg的层级为-1，使其在网页内容之下

    let simulation = d3.forceSimulation(data.nodes) // 创建一个力导向图模拟器，传入节点数据
      .force('link', d3.forceLink(data.links).id(d => d.id).distance(Distance)) // 添加一个连线力，传入边数据，设置边的id和距离属性
      .force('charge', d3.forceManyBody().strength(forceRate)) // 添加一个多体力，设置斥力系数属性
      .force('center', d3.forceCenter(width / 2, height / 2)); // 添加一个居中力，设置中心点坐标属性

    let link = svg.append('g') // 在svg元素中添加一个g元素，用于存放边元素
      .attr('class', 'links') // 设置g元素的类名为links
      .selectAll('line') // 选择所有的line元素（初始为空）
      .data(data.links) // 绑定边数据
      .enter().append('line') // 对于每个数据，添加一个line元素
      .attr('stroke-width', 1) // 设置线条的宽度为1像素
      .attr('stroke', '#999'); // 设置线条的颜色为灰色

    let node = svg.append('g') // 在svg元素中添加一个g元素，用于存放节点元素
      .attr('class', 'nodes') // 设置g元素的类名为nodes
      .selectAll('circle') // 选择所有的circle元素（初始为空）
      .data(data.nodes) // 绑定节点数据
      .enter().append('circle') // 对于每个数据，添加一个circle元素
      .attr('r', Radius) // 设置圆形的半径属性
      .attr('fill', color) // 设置圆形的填充颜色属性，根据消息的角色调用color函数返回颜色值
      .call(drag(simulation)); // 调用drag函数，传入模拟器对象，使节点可以被拖动

    node.append('title') // 在每个节点元素中添加一个title元素，用于显示提示信息
      .text(d => `${d.author}: ${d.text}`); // 设置title元素的文本内容为消息的作者和内容

    simulation.on('tick', () => {
      // 监听模拟器的tick事件，每次更新节点和边的位置
      link
        .attr('x1', d => d.source.x) // 设置线条的起点x坐标属性
        .attr('y1', d => d.source.y) // 设置线条的起点y坐标属性
        .attr('x2', d => d.target.x) // 设置线条的终点x坐标属性
        .attr('y2', d => d.target.y); // 设置线条的终点y坐标属性

      node
        .attr('cx', d => d.x) // 设置圆形的中心x坐标属性
        .attr('cy', d => d.y); // 设置圆形的中心y坐标属性
    });
  } catch (error) {
    console.error('Error drawing chart:', error); // 打印错误
  }
}

// 定义一个函数，用于创建一个拖动行为
function drag(simulation) {
  try {
    function dragstarted(event, d) {
      // 定义一个函数，用于处理拖动开始的事件
      if (!event.active) simulation.alphaTarget(0.3).restart(); // 如果模拟器不处于活跃状态，设置alpha目标值为0.3，并重启模拟器
      d.fx = d.x; // 设置节点的固定x坐标为当前x坐标
      d.fy = d.y; // 设置节点的固定y坐标为当前y坐标
    }

    function dragged(event, d) {
      // 定义一个函数，用于处理拖动进行中的事件
      d.fx = event.x; // 设置节点的固定x坐标为鼠标x坐标
      d.fy = event.y; // 设置节点的固定y坐标为鼠标y坐标
    }

    function dragended(event, d) {
      // 定义一个函数，用于处理拖动结束的事件
      if (!event.active) simulation.alphaTarget(0); // 如果模拟器不处于活跃状态，设置alpha目标值为0
      d.fx = null; // 取消节点的固定x坐标
      d.fy = null; // 取消节点的固定y坐标
    }

    return d3.drag() // 创建一个拖动行为对象
      .on('start', dragstarted) // 绑定拖动开始的事件处理函数
      .on('drag', dragged) // 绑定拖动进行中的事件处理函数
      .on('end', dragended); // 绑定拖动结束的事件处理函数
  } catch (error) {
    console.error('Error creating drag behavior:', error); // 打印错误
  }
}

// 定义一个函数，用于与background script通信
function handleMessage(request, sender, sendResponse) {
  try {
    if (request.type === 'start') {
      // 如果收到background script发送的开始可视化的请求，传入聊天数据
      drawChart(request.data); // 调用drawChart函数，传入聊天数据，绘制可视化图表
    } else if (request.type === 'stop') {
      // 如果收到background script发送的停止可视化的请求，传入响应信息
      console.log(request.data); // 打印响应信息
    }
  } catch (error) {
    console.error('Error handling message:', error); // 打印错误
  }
}

// 监听来自background script的消息
chrome.runtime.onMessage.addListener(handleMessage);
