                Debug.LogError("Erreur lors de la réception du message: " + e.Message);
            }
        }

        server.Close();
    }

    void OnDestroy()
    {
        isRunning = false;
        if (receiveThread != null && receiveThread.IsAlive)
        {
            receiveThread.Join();
        }
    }
}
``` 


Unity Receiver using AsyncCallback
``` csharp




using UnityEngine;
using System;
using System.Net;
using System.Net.Sockets;
using System.Text;

public class UDPReceiver : MonoBehaviour
{
    public int udpPort = 5005;

    private UdpClient m_udpClient;

    void Start()
    {
        try
        {
            // Initialisation du client UDP
            m_udpClient = new UdpClient(udpPort);
            
            // Démarrage de la réception asynchrone
            m_udpClient.BeginReceive(new AsyncCallback(ReceiveCallback), null);
            
            Debug.Log("En attente de messages...");
        }
        catch (Exception e)
        {
            Debug.LogError("Erreur lors de l'initialisation du récepteur UDP: " + e.Message);
        }
    }

    void ReceiveCallback(IAsyncResult result)
    {
        try
        {
            // Récupération des données reçues et de l'adresse de l'expéditeur
            IPEndPoint clientEndPoint = new IPEndPoint(IPAddress.Any, 0);
            byte[] receivedData = m_udpClient.EndReceive(result, ref clientEndPoint);

            // Conversion des données en chaîne de caractères
            string message = Encoding.UTF8.GetString(receivedData);

            // Affichage du message reçu
            Debug.Log("Message reçu: " + message);

            // Début d'une nouvelle réception asynchrone
            m_udpClient.BeginReceive(new AsyncCallback(ReceiveCallback), null);
        }
        catch (Exception e)
        {
            Debug.LogError("Erreur lors de la réception du message: " + e.Message);
        }
    }

    void OnDestroy()
    {
        // Fermeture du client UDP lors de la destruction de l'objet
        if (m_udpClient != null)
        {
            m_udpClient.Close();
        }
    }
}
```

Now that we have a prototype of delegated compute[^1] to move towards a paid API infrastructure we should make a dedicated endpoint that has secure access control. 

### Definitions

- Partner Repository `Partners` - this is where the work is being done.
- Permit Endpoint `Permits` - this is our paid endpoint, which is being prototyped on GitHub Actions now.

## Version I

A specialized version of the API that will be used specifically for our current use-case, which is calculating comment incentives, and assignee rewards. 

### `Permits` Input

This may be simpler to implement, as it will be specifically for the comment incentives. `Permits` will fetch the repository comments from the GitHub API using the `gitHubPersonalAccessToken`.

```typescript
const postBody = {
	evmPrivateKeyEncrypted: "xxx", // this can serve as an "API key" as they are associated with partners in our database.
	gitHubPersonalAccessToken: "gh_pat_0123", // with repository read permissions, especially for rate limiting and private repos
	eventName: "issues.closed",
	issueOwner: "ubiquity",
	issueRepository: "ubiquity-dollar",
	issueNumber: 128,
};
```

### `Permits` Output

We can just send the entire comment body with the permits as HTML.

### Fee Collection

- The simplest method is to generate an extra permit for us. We can charge 1% in Ubiquity Dollars or 5% in WXDAI, and we can automatically claim it from within the script.
- If the transaction is successful, we can post back the results. If the transaction is not successful, then we can throw an error.

