    mapping(uint256 => Mood) s_tokenIdToMood;

    constructor(
        string memory sadSvgImageUri,
        string memory happySvgImageUri
    ) ERC721("MoodNft", "MN") {
        s_tokenCounter = 0;
        s_sadSvgImageUri = sadSvgImageUri;
        s_happySvgImageUri = happySvgImageUri;
    }

    function mintNft() public {
        _safeMint(msg.sender, s_tokenCounter);
        // Default to happy
        s_tokenIdToMood[s_tokenCounter] = Mood.Happy;
        s_tokenCounter++;
    }

    function flipMood(uint256 tokenId) public {
        // Only the owner can change the mood
        // ERC721 built in method to check if the sender is the owner of X token
        if (!_isApprovedOrOwner(msg.sender, tokenId)) {
            revert MoodNft__CantFlipMoodIfNotOwner();
        }
        if (s_tokenIdToMood[tokenId] == Mood.Happy) {
            s_tokenIdToMood[tokenId] = Mood.Sad;
        } else {
            s_tokenIdToMood[tokenId] = Mood.Happy;
        }
    }

    function _baseURI() internal pure override returns (string memory) {
        return "data:application/json;base64,";
    }

    function tokenURI(
        uint256 tokenId
    ) public view override returns (string memory) {
        string memory imageURI;
        if (s_tokenIdToMood[tokenId] == Mood.Happy) {
            imageURI = s_happySvgImageUri;
        } else {
            imageURI = s_sadSvgImageUri;
        }

        return
            // We make this all a string
            string(
                // We concatenate the baseURI with the imageURI so that it starts with data:application/...
                abi.encodePacked(
                    _baseURI(),
                    // We encode it in base 64
                    Base64.encode(
                        // We convert it to bytes to use the OpenZeppelin Base64 encoder
                        bytes(
                            // abi.encodePacked concatenates all the strings
                            abi.encodePacked(
                                '{ "name": ',
                                name(),
                                '", "description":"An NFT that reflects the owners mood.", "attributes": [{"trait_type": "moodiness", "value":100}], "image":"',
                                imageURI,
                                '"}'
                            )
                        )
                    )
                )
            );
    }
}
```

